  /* ‚Äî CONSISTENT ALIASES ‚Äî */
  --primary: #4DD0E1;           
  --primary-light: #80DEEA;     
  --primary-dark: #26C6DA;      
  --secondary: #00BCD4;         
  --secondary-light: #4FC3F7;   
  --accent: #00ACC1;           
  --accent-light: #40C4FF;

  /* ‚Äî TEXT AND BACKGROUNDS ‚Äî */
  --text-primary: #1e293b;
  --text-secondary: #64748b;
  --text-muted: #94a3b8;
  --bg-primary: #f8fafc;
  --bg-secondary: #f1f5f9;
  --bg-card: #ffffff;
  --border-color: #e2e8f0;
  --glass-bg: rgba(255, 255, 255, 0.85);
  --shadow-color: rgba(77, 208, 225, 0.1);
  --shadow-lg: rgba(77, 208, 225, 0.15);

  /* ‚Äî SHARED ‚Äî */
  --border-radius: 12px;
  --border-radius-sm: 8px;
  --border-radius-lg: 16px;
  --transition: 0.3s ease;
  --spacing-xs: 0.25rem;
  --spacing-sm: 0.5rem;
  --spacing-md: 1rem;
  --spacing-lg: 1.5rem;
  --spacing-xl: 2rem;
}

/* Import notification styles */
@import url('notifications.css');

/* Base styling */
body {
