      transition: all 0.3s ease;
    }

    /* Form Container - Maximum Width Utilization */
    .form-container {
      flex: 1;
      min-width: 0;
      width: calc(100vw - 220px); /* Adjusted for smaller sidebar */
      max-width: calc(100vw - 220px);
      background: rgba(255, 255, 255, 0.98);
      backdrop-filter: blur(15px);
      border-radius: 0;
      padding: 16px; /* Reduced from 30px for more data area */
      box-shadow: none;
      border-left: 1px solid var(--border-color);
      margin: 0;
      box-sizing: border-box;
      overflow-x: auto; /* Allow horizontal scroll if needed */
    }

    /* Compact Vertical Progress Steps */
    .progress-nav {
      display: flex;
      flex-direction: column;
      gap: 4px; /* Reduced from 8px */
    }

    .progress-step {
      display: flex;
      align-items: center;
      gap: 12px; /* Reduced from 16px */
      padding: 8px 12px; /* Reduced from 16px */
      border-radius: var(--border-radius-sm);
      cursor: pointer;
      transition: var(--transition);
      border: 1px solid transparent; /* Reduced from 2px */
    }

.step-circle {
      width: 28px; /* Reduced from 40px */
