    }

    .module-details,
    .range-details {
        flex-direction: column;
        gap: 5px;
    }

    .btn-large {
        width: 100%;
        margin: 5px 0;
    }

    /* Mobile navigation adjustments */
    .modern-nav {
        padding: 8px 16px;
        flex-wrap: wrap;
        gap: 8px;
    }

    .nav-actions {
        flex-direction: row;
        justify-content: space-between;
        align-items: center;
        gap: 4px;
        width: 100%;
        flex-wrap: wrap;
    }

    .quick-table-access {
        order: 1;
        margin-right: 4px;
    }

    .save-progress-btn {
        order: 2;
        margin-right: 4px;
        padding: 6px 12px;
        font-size: 12px;
    }

    .user-profile {
        order: 3;
        min-width: auto;
    }

    .user-name {
        max-width: 100px;
    }

    .brand-text {
        display: none;
    }

    .quick-access-btn {
        padding: 6px 12px;
        font-size: 12px;
    }

    .quick-access-dropdown {
        min-width: 180px;
        left: -20px;
    }

    /* Mobile table improvements */
    .table-responsive {
        border-radius: 0;
        margin: 0.5rem 0;
    }

    table {
        min-width: 600px;
        font-size: 12px;
    }

    thead th {
        padding: 8px 6px;
        font-size: 12px;
    }

    tbody td {
        padding: 8px 6px;
        max-width: 120px;
    }

    tbody td input,
    tbody td textarea,
    tbody td select {
        min-width: 100px;
        font-size: 12px;
    }
}

/* ===== UNIFIED DASHBOARD STYLES ‚Äî */
.dashboard-container {
    min-height: 100vh;
    background: linear-gradient(135deg, var(--bg-primary) 0%, var(--bg-secondary) 100%);
    padding: 0;
    margin: 0;
    width: 100vw;
    overflow-x: hidden;
}

.dashboard-header {
    background: linear-gradient(135deg, var(--primary) 0%, var(--primary-dark) 100%);
    color: white;
    padding: var(--spacing-lg) var(--spacing-xl);
    box-shadow: 0 4px 20px var(--shadow-lg);
    margin-bottom: var(--spacing-xl);
}

.dashboard-header h1 {
    font-size: 2.25rem;
    font-weight: 700;
    margin: 0 0 var(--spacing-sm) 0;
    color: white;
}

.dashboard-header .subtitle {
    font-size: 1.1rem;
    opacity: 0.9;
    margin: 0;
    color: rgba(255, 255, 255, 0.9);
}

.dashboard-content {
    max-width: 1400px;
    margin: 0 auto;
    padding: 0 var(--spacing-xl) var(--spacing-xl);
    width: 100%;
    box-sizing: border-box;
}

.dashboard-nav {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: var(--bg-card);
    padding: var(--spacing-md) var(--spacing-lg);
    border-radius: var(--border-radius);
    box-shadow: 0 2px 10px var(--shadow-color);
    margin-bottom: var(--spacing-xl);
    border: 1px solid var(--border-color);
}

.nav-links {
    display: flex;
    gap: var(--spacing-lg);
    align-items: center;
}

.nav-link {
    display: flex;
    align-items: center;
    gap: var(--spacing-sm);
    padding: var(--spacing-sm) var(--spacing-md);
    border-radius: var(--border-radius-sm);
    text-decoration: none;
    color: var(--text-secondary);
    font-weight: 500;
    transition: var(--transition);
    border: 1px solid transparent;
}

.nav-link:hover {
    background: var(--bg-secondary);
    color: var(--primary);
    border-color: var(--border-color);
}

.nav-link.active {
    background: var(--primary);
    color: white;
}

.user-info {
    display: flex;
    align-items: center;
    gap: var(--spacing-md);
    position: relative;
}

.user-avatar {
    width: 40px;
    height: 40px;
    background: linear-gradient(135deg, var(--primary) 0%, var(--secondary) 100%);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-weight: 600;
    font-size: 16px;
}

.user-details {
    cursor: pointer;
    padding: var(--spacing-sm);
    border-radius: var(--border-radius-sm);
    transition: var(--transition);
    position: relative;
}

.user-details:hover {
    background: var(--bg-secondary);
}

.user-details h4 {
    margin: 0;
    color: var(--text-primary);
    font-size: 14px;
    font-weight: 600;
    display: flex;
    align-items: center;
    gap: var(--spacing-xs);
}

.user-details h4::after {
    content: '‚ñº';
    font-size: 10px;
    transition: transform var(--transition);
}

.user-details.open h4::after {
    transform: rotate(180deg);
}

/* User Dropdown Menu */
.user-dropdown {
    position: absolute;
    top: 100%;
    right: 0;
    background: var(--bg-card);
    border: 1px solid var(--border-color);
    border-radius: var(--border-radius);
    box-shadow: 0 4px 20px var(--shadow-lg);
    min-width: 220px;
    z-index: 1000;
    opacity: 0;
    visibility: hidden;
    transform: translateY(-10px);
    transition: all var(--transition);
}

.user-dropdown.open {
    opacity: 1;
    visibility: visible;
    transform: translateY(0);
}

.dropdown-item {
    display: flex;
    align-items: center;
    gap: var(--spacing-sm);
    padding: var(--spacing-md);
    color: var(--text-primary);
    text-decoration: none;
    transition: var(--transition);
    border-bottom: 1px solid var(--border-color);
}

.dropdown-item:last-child {
    border-bottom: none;
}

.dropdown-item:hover {
    background: var(--bg-secondary);
    color: var(--primary);
}

.dropdown-item.logout {
    color: #ef4444;
}

.dropdown-item.logout:hover {
    background: #fee2e2;
    color: #dc2626;
}

.dropdown-item i {
    width: 16px;
    text-align: center;
}

.dropdown-item .item-description {
    font-size: 12px;
    color: var(--text-muted);
    margin-top: 2px;
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: var(--spacing-lg);
    margin-bottom: var(--spacing-xl);
}

.stat-card {
    background: var(--bg-card);
    border-radius: var(--border-radius);
    padding: var(--spacing-lg);
    box-shadow: 0 4px 16px var(--shadow-color);
    border: 1px solid var(--border-color);
    position: relative;
    overflow: hidden;
    transition: var(--transition);
}

.stat-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 30px var(--shadow-lg);
}

.stat-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
}

.stat-card.primary::before {
    background: linear-gradient(90deg, var(--primary), var(--primary-light));
}

.stat-card.secondary::before {
    background: linear-gradient(90deg, var(--secondary), var(--secondary-light));
}

.stat-card.accent::before {
    background: linear-gradient(90deg, var(--accent), var(--accent-light));
}

.stat-card.info::before {
    background: linear-gradient(90deg, var(--primary-light), var(--primary));
}

.stat-icon {
    width: 56px;
    height: 56px;
    border-radius: var(--border-radius);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 24px;
    margin-bottom: var(--spacing-md);
}

.stat-card.primary .stat-icon {
    background: rgba(59, 130, 246, 0.1);
    color: var(--primary);
}

.stat-card.secondary .stat-icon {
    background: rgba(5, 150, 105, 0.1);
    color: var(--secondary);
}

.stat-card.accent .stat-icon {
    background: rgba(245, 158, 11, 0.1);
    color: var(--accent);
}

.stat-card.info .stat-icon {
    background: rgba(59, 130, 246, 0.1);
    color: var(--primary-light);
}

.stat-number {
    font-size: 2rem;
    font-weight: 700;
    color: var(--text-primary);
    line-height: 1;
    margin-bottom: var(--spacing-xs);
}

.stat-label {
    color: var(--text-secondary);
    font-size: 14px;
    font-weight: 500;
    margin-bottom: var(--spacing-sm);
}

.stat-change {
    font-size: 12px;
    font-weight: 600;
    display: flex;
    align-items: center;
    gap: var(--spacing-xs);
}

.stat-change.positive {
    color: var(--secondary);
}

.stat-change.negative {
    color: #ef4444;
}

.dashboard-section {
    background: var(--bg-card);
    border-radius: var(--border-radius);
    padding: var(--spacing-lg);
    margin-bottom: var(--spacing-lg);
    box-shadow: 0 2px 10px var(--shadow-color);
    border: 1px solid var(--border-color);
}

.section-header {
    display: flex;
    justify-content: between;
    align-items: center;
    margin-bottom: var(--spacing-lg);
    padding-bottom: var(--spacing-md);
    border-bottom: 2px solid var(--border-color);
}

.section-header h2 {
    color: var(--text-primary);
    font-size: 1.5rem;
    font-weight: 600;
    margin: 0;
}

.quick-actions {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: var(--spacing-md);
    margin-bottom: var(--spacing-xl);
}

.action-card {
    background: var(--bg-card);
    border: 2px solid var(--border-color);
    border-radius: var(--border-radius);
    padding: var(--spacing-lg);
    text-align: center;
    transition: var(--transition);
    cursor: pointer;
    text-decoration: none;
    color: var(--text-primary);
}

.action-card:hover {
    border-color: var(--primary);
    transform: translateY(-2px);
    box-shadow: 0 8px 25px var(--shadow-lg);
    color: var(--text-primary);
}

.action-icon {
    font-size: 2.5rem;
    color: var(--primary);
    margin-bottom: var(--spacing-md);
}

.action-card h3 {
    margin: 0 0 var(--spacing-sm) 0;
    color: var(--text-primary);
    font-size: 1.1rem;
    font-weight: 600;
}

.action-card p {
    margin: 0;
    color: var(--text-secondary);
    font-size: 14px;
    line-height: 1.4;
}

/* Mobile Dashboard Responsiveness */
@media (max-width: 768px) {
    .dashboard-header {
        padding: var(--spacing-md);
    }

    .dashboard-header h1 {
        font-size: 1.75rem;
    }

    .dashboard-content {
        padding: 0 var(--spacing-md) var(--spacing-md);
    }

    .dashboard-nav {
        flex-direction: column;
        gap: var(--spacing-md);
        align-items: stretch;
    }

    .nav-links {
        flex-wrap: wrap;
        justify-content: center;
    }

    .stats-grid {
        grid-template-columns: 1fr;
        gap: var(--spacing-md);
    }

    .quick-actions {
        grid-template-columns: 1fr;
        gap: var(--spacing-md);
    }

    .user-info {
        justify-content: center;
        text-align: center;
    }
}

/* ===== MAIN CONTENT LAYOUT ===== */
.main-content {
  flex-grow: 1;
  padding: 20px 24px;
  background-color: #f8f9fa;
  overflow-y: auto; /* Allow scrolling within the main content area */
}

/* Adjustments for the dashboard layout */
body.dashboard-layout .main-content {
  padding: 0; /* Reset padding for dashboard */
  background: linear-gradient(135deg, #E3F4F4 0%, #D1E9FE 50%, #E3F4F4 100%);
}

/* Specific padding for the form container within the main layout */
.main-layout .form-container {
  padding: 20px 24px;
}

/* Responsive adjustments for the main content */
@media (max-width: 1200px) {
  .main-layout .form-container {
    padding: 16px;
  }
}

@media (max-width: 768px) {
  .main-content {
    padding: 16px;
  }
  .main-layout .form-container {
    padding: 12px;
  }
}

/* ===== FINAL RESPONSIVE LAYOUT ADJUSTMENTS ===== */

/* Ensure sidebar and content take up appropriate space */
.main-layout {
  display: flex;
  flex-direction: row; /* Default to row layout */
  min-height: calc(100vh - 50px); /* Adjusted for sticky header */
  width: 100vw;
  max-width: 100vw;
  margin: 0;
  gap: 0;
  padding: 0;
  overflow-x: hidden; /* Prevent horizontal scroll on the main layout */
}

/* Sidebar adjustments */
.progress-sidebar {
  width: 220px; /* Default width */
  min-width: 220px;
  flex-shrink: 0;
  background: rgba(255, 255, 255, 0.98);
  backdrop-filter: blur(10px);
  border-radius: 0;
  padding: 16px;
  height: fit-content;
  position: sticky;
  top: 50px; /* Position below the sticky header */
  box-shadow: 1px 0 8px rgba(0,0,0,0.06);
  border-right: 1px solid var(--border-color);
  transition: all 0.3s ease;
}

/* Content area adjustments */
.form-container {
  flex: 1;
  min-width: 0;
  width: calc(100vw - 220px); /* Takes remaining space */
  max-width: calc(100vw - 220px);
  background: rgba(255, 255, 255, 0.98);
  backdrop-filter: blur(15px);
  border-radius: 0;
  padding: 16px;
  box-shadow: none;
  border-left: 1px solid var(--border-color);
  margin: 0;
  box-sizing: border-box;
  overflow-x: auto; /* Allow horizontal scroll if content exceeds width */
}

/* Media query for tablets and smaller desktops */
@media (max-width: 1200px) {
  .progress-sidebar {
    width: 180px; /* Narrower sidebar */
    min-width: 180px;
    padding: 12px;
  }
  .form-container {
    width: calc(100vw - 180px); /* Adjust content width */
    max-width: calc(100vw - 180px);
    padding: 12px;
  }
}

/* Media query for tablets */
@media (max-width: 991px) {
  .main-layout {
    flex-direction: column; /* Stack sidebar and content */
    min-height: calc(100vh - 50px); /* Adjust height */
  }
  .progress-sidebar {
    width: 100%;
    min-width: auto;
    position: relative; /* Reset position */
    top: auto;
    border-radius: 0;
    border-right: none;
    border-bottom: 1px solid var(--border-color);
    padding: 15px;
    height: auto; /* Adjust height */
  }
  .form-container {
    width: 100%;
    max-width: 100%;
    border-left: none; /* Remove left border */
    margin: 0;
    padding: 15px;
    overflow-x: hidden; /* Ensure no horizontal scroll */
  }
}

/* Media query for mobile devices */
@media (max-width: 768px) {
  .main-layout {
    min-height: calc(100vh - 50px); /* Adjust height */
  }
  .progress-sidebar {
    padding: 10px 12px;
  }
  .form-container {
    padding: 10px;
  }
  /* Further tablet adjustments */
  .table-responsive {
    margin: 8px 0; /* Reduce margin */
    border-radius: 8px; /* Smaller border radius */
  }
  table {
    min-width: 600px; /* Ensure minimum width for horizontal scroll */
    font-size: 12px; /* Smaller font size */
  }
  thead th, tbody td {
    padding: 8px 6px; /* Reduced padding */
    font-size: 11px; /* Smaller font size */
  }
  tbody td input, tbody td select, tbody td textarea {
    font-size: 12px; /* Smaller input font size */
    padding: 6px 8px;
  }
}

/* Media query for very small mobile devices */
@media (max-width: 480px) {
  .main-layout {
    min-height: calc(100vh - 40px); /* Further adjust height */
  }
  .progress-sidebar {
    padding: 6px 8px; /* More compact sidebar */
  }
  .form-container {
    padding: 6px 8px; /* More compact content */
  }
  table {
    min-width: 400px; /* Further reduce minimum width */
    font-size: 10px; /* Smaller font size */
  }
  thead th, tbody td {
    padding: 4px 2px; /* Even less padding */
    font-size: 10px; /* Smaller font size */
  }
  tbody td input, tbody td select, tbody td textarea {
    font-size: 10px; /* Smallest input font size */
    padding: 3px 4px;
  }
}

